    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
         String text = valorText.getText();
         double valorDouble = Double.parseDouble(text);
         double calculo = (valorDouble*0.025)*12;
         String calculoconvertido = Double.toString(calculo);
         impostoJbl.setText(calculoconvertido +"$");
    }//GEN-LAST:event_jButton1ActionPerformed
