    private void jButton9ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton9ActionPerformed
         String text1 = valor2Text.getText();
         Double valorDouble2 = Double.parseDouble(text1);
         double calculo2 = ((valorDouble2*0.050)*12) + 10;
         String calculoconvertido2 = Double.toString(calculo2);
         imposto2Jbl.setText(calculoconvertido2 +"$");
    }//GEN-LAST:event_jButton9ActionPerformed
